import React, { Component } from 'react';
import './App.css';
import Axios from 'axios';
import GitHubRepos from './GiHubRepos';
import GitHubUser from './GitHubUser';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      user: {},
      repos: []
    }
    this.handleInputChange = this.handleInputChange.bind(this);
    this.getUser = this.getUser.bind(this);
  }

  getUser() {
    // var user = "SalithaUCSC";

    if (this.state.user != null) {
      Axios.get("https://api.github.com/users/"+ this.state.user)
      .then(res => {
        this.setState({user: res.data});
        console.log(this.state.user); 
      })

      .catch(err => console.log(err));
      Axios.get("https://api.github.com/users/"+ this.state.user + "/repos")
      .then(res => {
        this.setState({repos: res.data});
        console.log(this.state.repos); 
      })
      .catch(err => console.log(err));
    }

  }

  handleInputChange(e) {
    this.setState({username: e.target.value})
  } 


  render() {
    return (
      <div className="App" style={{marginTop: "50px"}}>
        <div className="container">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Type a GitHub Username</h5>
              <input className="form-control" name="username" onChange={this.handleInputChange} style={{marginLeft: "0px", display:"block"}}/>
              {this.state.username === this.state.user.username ? <GitHubUser user={this.state.user}/> : null}
              {this.state.user && this.state.repos.length > 0 ? <GitHubRepos repos={this.state.repos} user={this.state.user}/> : null}
              <button className="btn btn-primary" onClick={this.getUser}>get</button>
            </div>
          </div>
        </div>
      </div>

    );
  }
}

export default App;
