import React, { Component } from 'react';
import './App.css';
import Axios from 'axios';

class GitHubRepos extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    var gituser = this.props.user;
    var gitrepos = this.props.repos;
    return (
      <div className="App" style={{marginTop: "50px"}}>
        <div className="container">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title text-left">{gituser.name}'s Public Repositories</h4><hr/>
              {                 
                  gitrepos.length > 0 ?
                    gitrepos.map((repo, i) => {
                        return  (
                            <div className="row" key={i}>
                                <div className="col-lg-9 text-left" style={{marginBo}}>
                                    <h5>{repo.full_name}</h5>
                                    <p>{repo.description}</p>
                                </div>
                                <div className="col-lg-3">
                                    <div className="row">
                                        <span className="badge badge-primary">{repo.watchers}</span><br/>
                                        <span className="badge badge-success">{gituser.forks}</span>
                                    </div>
                                </div>

                            </div>
                        );
                    })
                  : null
              }
            </div>
          </div>
        </div>
      </div>

    );
  }
}

export default GitHubRepos;
