import React, { Component } from 'react';
import './App.css';
import Axios from 'axios';
import GitHubRepos from './GiHubRepos';
import GitHubUser from './GitHubUser';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      user: [],
      repos: []
    }
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  componentDidMount() {
    var user = "SalithaUCSC";
    Axios.get("https://api.github.com/users/"+ user+ "/repos")
    .then(res => {
      this.setState({repos: res.data});
      console.log("repos: ",this.state.repos); 
    })
    .catch(err => console.log(err));
  }

  handleInputChange(e) {
    this.setState({username: e.target.value})
  } 

  render() {
    return (
      <div className="App" style={{marginTop: "50px"}}>
        <div className="container">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Type a Username</h5>
              <input className="form-control" onChange={this.handleInputChange}/>
              {this.state.user.length > 0 ? <GitHubUser user={this.state.repos}/> : null}
              {this.state.repos.length > 0 ? <GitHubRepos repos={this.state.repos}/> : null}
            </div>
          </div>
        </div>
      </div>

    );
  }
}

export default App;
